import {getLogger} from "../core";
import React, {useCallback, useEffect, useReducer, useState} from "react";
import {TrainProps} from "./TrainProps";
import PropTypes from 'prop-types';
import {getTrains, newWebSocket, updateTrain} from "./TrainsAPI";


const log = getLogger('useTrains');

type  BuyTicketFn = (train: TrainProps) => Promise<any>;

export interface TrainsState{
    trains?: TrainProps[],
    fetching: boolean,
    fetchingError?: Error|null,
    buying: boolean,
    buyingError?: Error | null,
    buyTicket?: BuyTicketFn,
}


interface ActionProps{
    type:string,
    payload?: any,
}

const initialState: TrainsState = {
    fetching: false,
    buying: false,
}

const FETCH_TRAINS_STARTED = 'FETCH_TRAINS_STARTED';
const FETCH_TRAINS_SUCCEDED = 'FETCH_TRAINS_SUCCEDED';
const FETCH_TRAINS_FAILED = 'FETCH_TRAINS_FAILED';
const BUYING_TICKET_STARTED = 'BUYING_TICKET_STARTED';
const BUYING_TICKET_SUCCEDED = 'BUYING_TICKET_SUCCEDED';
const BUYING_TICKET_FAILED = 'BUYING_TICKET_FAILED';

const reducer: (state: TrainsState, action: ActionProps) => TrainsState =
    (state, {type, payload}) =>{
        switch (type){
            case FETCH_TRAINS_STARTED:
                return{...state, fetching: true};
            case FETCH_TRAINS_SUCCEDED:
                log(payload.trains);
                return {...state, trains: payload.trains, fetching: false};
            case FETCH_TRAINS_FAILED:
                return {...state, fetchingError:payload.error, fetching: false};
            case BUYING_TICKET_STARTED:
                return {...state, buyingError: null, buying:true};
            case BUYING_TICKET_SUCCEDED:
                const trains = [...(state.trains || [])];
                const train = payload.train;
                log(trains);
                const index = trains.findIndex(it => it.id === train.id);
                log(index);
                if(index === -1){
                    trains.splice(0, 0, train);
                }else {
                    trains[index] = train;
                }
                return {...state, trains, buying: false};
            case BUYING_TICKET_FAILED:
                return {...state, buyingError: payload.error, buying:false};
            default:
                return state;
        }
    };

export const TrainContext = React.createContext<TrainsState>(initialState);

interface TrainProviderProps{
    children: PropTypes.ReactNodeLike,
}

export const TrainProvider: React.FC<TrainProviderProps> = ({}) =>{
    const [state, dispatch] = useReducer(reducer, initialState);
    const {trains, fetching, fetchingError, buying, buyingError} = state;
    useEffect(getTrainsEffect, []);
    useEffect(wsEffect, []);
    const buyTicket = useCallback<BuyTicketFn>(buyTicketCallback, []);
    const value = {trains, fetching, fetchingError, buying, buyingError, buyTicket};
    log('return');
    return;

    function getTrainsEffect() {
        let canceled = false;
        fetchTrains();
        return () => {
            canceled = true;
        }

        async function fetchTrains() {
            try {
                log('Trying to fetch trains...');
                dispatch({type: FETCH_TRAINS_STARTED});
                const trains = await getTrains();
                log("Yaay trains fetched");
                if (!canceled) {
                    dispatch({type: FETCH_TRAINS_SUCCEDED, payload: {trains}});
                }
            } catch (error) {
                log("Error sorry (not trains got)");
                dispatch({type: FETCH_TRAINS_FAILED, payload: {error}});
            }
        }
    }

    async function buyTicketCallback(train: TrainProps){
        try{
            log('Buying ticket started (pls God)');
            dispatch({type: BUYING_TICKET_STARTED});
            const updatedTrain = await (train.id ? updateTrain(train) : updateTrain(train));
            log("Ticket bought...");
            dispatch({type: BUYING_TICKET_SUCCEDED, payload:{train:updatedTrain}});
        }catch (error){
            log("Failed buying ticket (Sad face)");
            dispatch({type: BUYING_TICKET_FAILED, payload:{error}});
        }
    }

    function wsEffect(){
        let canceled = false;
        log('wsEffect - connecting');
        const closeWebSocket = newWebSocket(message => {
            if(canceled){
                return;
            }
            const {event, payload: {train}} = message;
            log(`ws message item ${event}`);
            if(event === 'updated'){
                dispatch({type: BUYING_TICKET_SUCCEDED, payload:{train}});
            }
        });
        return() => {
            log('wsEffect - disconnecting');
            canceled = true;
            closeWebSocket();
        }
    }
};
