import {
    IonContent,
    IonFab,
    IonFabButton,
    IonHeader,
    IonIcon, IonList,
    IonLoading,
    IonPage,
    IonTitle,
    IonToolbar
} from "@ionic/react";
import React, {useContext, useState} from "react";
import Train from "./Train";
import { getLogger } from '../core';
import {add, train} from "ionicons/icons";
import {TrainContext} from "./TrainProvider";
import {RouteComponentProps} from "react-router";

const log = getLogger('Home');

const Home: React.FC<RouteComponentProps> = ({history}) =>{
    const {trains, fetching, fetchingError} = useContext(TrainContext);
    log("Trains render...");
    return(
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>Student CFR</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                <IonLoading isOpen = {fetching} message = "Fetching trains"/>
                {trains && (
                    <IonList>
                        {trains.map(({id, from, to, nrSeats}) => <Train id={id} from={from} to={to} nrSeats={nrSeats}/>)}
                    </IonList>
                )}
                {fetchingError && (
                    <div>{fetchingError.message || 'Failed to fetch items'}</div>
                )}
                <IonFab vertical="bottom" horizontal="end" slot="fixed">
                    <IonFabButton onClick={() => history.push('/train')}>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>
            </IonContent>
        </IonPage>
    );
};

export default Home;
