import React, { useContext, useEffect, useState } from 'react';
import {
    IonButton,
    IonButtons,
    IonContent, IonFab, IonFabButton,
    IonHeader,
    IonInput, IonList,
    IonLoading,
    IonPage,
    IonTitle,
    IonToolbar,
    IonIcon, IonGrid, IonRow, IonCol, IonImg, IonActionSheet
} from '@ionic/react';
import { getLogger } from '../core';
import { TrainContext } from './TrainProvider';
import { RouteComponentProps } from 'react-router';
import { TrainProps } from './TrainProps';
import {Plugins} from "@capacitor/core";
import {camera, trash, close, star} from 'ionicons/icons';

import Train, {TrainDetails} from "./Train";
import {Photo, usePhotoGallery} from "./usePhotoGallery";
import {AuthContext} from "../auth";

const log = getLogger('ItemEdit');

interface ItemEditProps extends RouteComponentProps<{
    id?: string;
}> {}

const ItemEdit: React.FC<ItemEditProps> = ({ history, match }) => {
    const { trains, buying, buyingError, buyTicket } = useContext(TrainContext);
    const {token} = useContext(AuthContext);
    const [text, setText] = useState('');
    const [item, setItem] = useState<TrainProps>();
    const [localItem, setLocalItem] = useState<TrainProps>();

    const { photos, takePhoto, deletePhoto, uploadPhoto } = usePhotoGallery();
    const [photoToDelete, setPhotoToDelete] = useState<Photo>();

    useEffect(() => {
        log('useEffect');
        const routeId = match.params.id || '';
        const item = trains?.find(it => it._id === routeId);
        setItem(item);
        if (item) {
            log('Exista');
        }else {
            log("Nu exista, incercam local")
            getLocal(routeId);
        }
    }, [match.params.id, trains]);

    function getLocal(routeId: string){
        (async () => {
            const {Storage} = Plugins;
            const res = await Storage.get({key: routeId});
            if (res.value) {
                console.log('Train found', JSON.parse(res.value));
                setItem(JSON.parse(res.value));
            } else {
                console.log('Train not found');
            }
        })();
    }

    const handleBuy = () => {
        const editedItem = item;
        if(item) {
            buyTicket && buyTicket(item).then(() => history.goBack());
        }
    };
    log('render');
    return (
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>Edit</IonTitle>
                    <IonButtons slot="end">
                        <IonButton onClick={handleBuy}>
                            Buy
                        </IonButton>
                    </IonButtons>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                {item && (
                    <TrainDetails _id={item._id} from={item.from} to={item.to} timeLeave={item.timeLeave} timeArrive={item.timeArrive} nrSeats={item.nrSeats}/>
                )}
                {item && (
                    <img src={item?.imgSrc} alt="no image"/>
                )}
                <IonGrid>
                    <IonRow>
                        {photos.map((photo, index) => (
                            <IonCol size="6" key={index}>
                                <IonImg onClick={() => setPhotoToDelete(photo)}
                                        src={photo.webviewPath}/>
                            </IonCol>
                        ))}
                    </IonRow>
                </IonGrid>
                <IonFab vertical="bottom" horizontal="center" slot="fixed">
                    <IonFabButton onClick={() => {
                        const photoTaken = takePhoto();
                        
                    }}>
                        <IonIcon icon={camera}></IonIcon>
                    </IonFabButton>
                </IonFab>
                <IonActionSheet
                    isOpen={!!photoToDelete}
                    buttons={[{
                        text: 'Upload',
                        icon: star,
                        handler: () => {
                            handleBuy();
                        }
                    },{
                        text: 'Delete',
                        role: 'destructive',
                        icon: trash,
                        handler: () => {
                            if (photoToDelete) {
                                deletePhoto(photoToDelete);
                                setPhotoToDelete(undefined);
                            }
                        }
                    }, {
                        text: 'Cancel',
                        icon: close,
                        role: 'cancel'
                    }]}
                    onDidDismiss={() => setPhotoToDelete(undefined)}
                />
            </IonContent>
        </IonPage>
    );
};

export default ItemEdit;
