import {getLogger} from "../core";
import {RouteComponentProps} from "react-router";
import React, {useContext, useEffect, useState} from "react";
import {TrainContext} from "./TrainProvider";
import {TrainProps} from "./TrainProps";
import {IonButton, IonButtons, IonContent, IonHeader, IonLoading, IonPage, IonTitle} from "@ionic/react";
import {TrainDetails} from "./Train";

const log = getLogger('BuyTicket');

interface BuyTicketProps extends RouteComponentProps<{
    id?: string;
}>{}

const BuyTicket: React.FC<BuyTicketProps> = ({history, match}) =>{
    const {trains, buying, buyingError, buyTicket} = useContext(TrainContext);
    const [text, setText] = useState('');
    const [train, setTrain] = useState<TrainProps>();
    useEffect(()=>{
        log('useEffect');
        const routeId = match.params.id || '';
        const train = trains?.find(it => it.id = routeId);
        setTrain(train);
    }, [match.params.id, trains]);
    const handleBuy = () => {
        if(train) {
            buyTicket && buyTicket(train).then(() => history.goBack());
        }else log("Nu este ales tren");
    };
    log('render BuyTicket');
    return (
        <IonPage>
            <IonHeader>
                <IonTitle>Buy Ticket</IonTitle>
                <IonButtons slot = "end">
                    <IonButton>
                        Buy
                    </IonButton>
                </IonButtons>
            </IonHeader>
            <IonContent>

            </IonContent>
        </IonPage>
    );
}

export default BuyTicket;
