import {useContext, useEffect, useState} from 'react';
import { NetworkStatus, Plugins } from '@capacitor/core';
import {useBackgroundTask} from "./useBackgroundTask";
import {TrainProps} from "../todo/TrainProps";


const { Network } = Plugins;

const initialState = {
  connected: false,
  connectionType: 'unknown',
}

function buildTrainProps(train: TrainProps){
  return train;
}

export const useNetwork = () => {
  const [networkStatus, setNetworkStatus] = useState(initialState)
  useEffect(() => {
    const handler = Network.addListener('networkStatusChange', handleNetworkStatusChange);
    Network.getStatus().then(handleNetworkStatusChange);
    let canceled = false;
    return () => {
      canceled = true;
      handler.remove();
    }

    function handleNetworkStatusChange(status: NetworkStatus) {
      console.log('useNetwork - status change', status);
      if (!canceled) {
        if(status.connected){
          useBackgroundTask(() => new Promise(resolve => {
            console.log('My Background Task');
            resolve();
          }));
        }
        setNetworkStatus(status);
      }
    }
  }, [])
  return { networkStatus };
};
