import {IonContent, IonFab, IonFabButton, IonHeader, IonIcon, IonPage, IonTitle, IonToolbar} from "@ionic/react";
import React, {useState} from "react";
import Train from "./Train";
import { getLogger } from '../core';
import {add} from "ionicons/icons";

const log = getLogger('Home');

const Home: React.FC = () =>{

    const [trains, setItems] = useState([
        {id: "1", from:"Bucuresti", to:"Iasi", nrSeats: 4},
        {id: "2", from:"Cluj-Napoca", to:"Timisoara", nrSeats: 5}
    ]);

    const changeSeats = () => {
        const id = "2";
        log("Seat changed");
        let upTrain = trains.find(tr => tr.id === id );
        if(upTrain){
            upTrain.nrSeats++;
            //let trainsAux = trains;
            for(let i = 0; i < trains.length; i++){
                if(trains[i].id === upTrain.id)
                    trains[i].nrSeats = upTrain.nrSeats;
            }
            const newTrains = trains
            setItems(trains.reverse());
            log("Trains render...")
        }
    }

    return(
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>My App</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                {trains.map(({from, to, nrSeats}) => <Train from={from} to={to} nrSeats={nrSeats}/>)}
                <IonFab vertical="bottom" horizontal="end" slot="fixed">
                    <IonFabButton onClick={changeSeats}>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>
            </IonContent>
        </IonPage>
    );
};

export default Home;
