import {
    IonContent,
    IonFab,
    IonFabButton,
    IonHeader,
    IonIcon, IonInfiniteScroll, IonInfiniteScrollContent, IonList,
    IonLoading,
    IonPage,
    IonTitle,
    IonToolbar, useIonViewWillEnter
} from "@ionic/react";
import React, {useContext, useState} from "react";
import Train from "./Train";
import { getLogger } from '../core';
import {add, train} from "ionicons/icons";
import {TrainContext, TrainProvider} from "./TrainProvider";
import {RouteComponentProps} from "react-router";
import {getTrains, getTrainsPage} from "./TrainsAPI";
import {AuthContext} from "../auth";

const log = getLogger('Home');

const Home: React.FC<RouteComponentProps> = ({history}) =>{
    let page = 1;
    let {trains, fetching, fetchingError} = useContext(TrainContext);
    const {token} = useContext(AuthContext);

    const [items, setItems] = useState<string[]>([]);
    const [disableInfiniteScroll, setDisableInfiniteScroll] = useState<boolean>(false);


    async function fetchItems() {
        try {
            log('fetchItems started');
            const trains = await getTrainsPage(token, page);
            setItems([...trains, ...trains.me]);
            log('fetchItems succeeded');
        } catch (error) {
            log('fetchItems failed');
        }
    }

    useIonViewWillEnter(async () => {
        await fetchItems();
    });

    async function searchNext($event: CustomEvent<void>) {
        //const {trains, fetching, fetchingError} = useContext(TrainContext);
        log("searching Next");
        await fetchItems();
        ($event.target as HTMLIonInfiniteScrollElement).complete();
    }

    log("Trains render...");
    return(
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>Student CFR</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                <IonLoading isOpen = {fetching} message = "Fetching trains"/>
                {trains && (
                    <IonList>
                        {trains.map(({_id, from, to, nrSeats}) =>
                            <Train key={_id} _id={_id} from={from} to={to} nrSeats={nrSeats} onBuy={id => history.push(`/train/${id}`)}/>)}
                    </IonList>
                )}
                {fetchingError && (
                    <div>{fetchingError.message || 'Failed to fetch items'}</div>
                )}
                <IonInfiniteScroll threshold="100px" disabled={disableInfiniteScroll}
                                   onIonInfinite={(e: CustomEvent<void>) => searchNext(e)}>
                    <IonInfiniteScrollContent
                        loadingText="Loading...">
                    </IonInfiniteScrollContent>
                </IonInfiniteScroll>
            </IonContent>
        </IonPage>
    );
};

export default Home;
