import {getLogger} from "../core";
import {RouteComponentProps} from "react-router";
import React, {useContext, useEffect, useState} from "react";
import {TrainContext} from "./TrainProvider";
import {TrainProps} from "./TrainProps";
import {
    IonButton,
    IonButtons,
    IonContent, IonFab,
    IonFabButton,
    IonHeader,
    IonIcon,
    IonLoading,
    IonPage,
    IonTitle
} from "@ionic/react";
import {TrainDetails} from "./Train";
import {add} from "ionicons/icons";

const log = getLogger('BuyTicket');

interface BuyTicketProps extends RouteComponentProps<{
    id?: string;
}>{}

const BuyTicket: React.FC<BuyTicketProps> = ({history, match}) =>{
    const {trains, buying, buyingError, buyTicket} = useContext(TrainContext);
    const [text, setText] = useState('');
    const [train, setTrain] = useState<TrainProps>();
    useEffect(()=>{
        log('useEffect');
        const routeId = match.params.id || '';
        log(trains);
        const train = trains?.find(it => it.id = routeId);
        log(train);
        setTrain(train);
    }, [match.params.id, trains]);
    const handleBuy = () => {
        if(train) {
            buyTicket && buyTicket(train).then(() => history.goBack());
        }else log("Nu este ales tren");
    };
    //log(train);
    log('render BuyTicket');
    return (
        <IonPage>
            <IonHeader>
                <IonTitle>Buy Ticket</IonTitle>
            </IonHeader>
            <IonContent>
                {train && (
                    <TrainDetails key ={train.id} from={train.from} to={train.to} nrSeats={train.nrSeats} timeArrive={train.timeArrive} timeLeave={train.timeLeave}/>
                )}
                <IonLoading isOpen = {buying}/>
                {buyingError && (
                    <div>{buyingError.message || 'Failed to save item'}</div>
                )}

                <IonFab vertical="bottom" horizontal="end" slot="fixed">
                    <IonFabButton onClick={handleBuy}>
                        <IonIcon icon={add} />
                    </IonFabButton>
                </IonFab>
            </IonContent>
        </IonPage>
    );
}

export default BuyTicket;
