import {IonContent, IonHeader, IonPage, IonTitle, IonToolbar} from "@ionic/react";
import React, {useState} from "react";
import Train from "./Train";
import { getLogger } from '../core';

const log = getLogger('Home');

const Home: React.FC = () =>{

    const [trains, setItems] = useState([
        {id: "1", from:"Bucuresti", to:"Iasi", nrSeats: 4},
        {id: "2", from:"Cluj-Napoca", to:"Timisoara", nrSeats: 5}
    ]);

    const changeSeats = () => {
        const id = "2";
        log("Seat changed");
        let upTrain = trains.find(tr => tr.id === id );
        if(upTrain){
            upTrain.nrSeats++;
            let trainsAux = trains;
            for(let i = 0; i < trains.length; i++){
                if(trainsAux[i].id === upTrain.id)
                    trainsAux[i].nrSeats = upTrain.nrSeats;
            }
            setItems(trainsAux);
            log("Trains render...")
        }
    }

    return(
        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>My App</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                {trains.map(({from, to, nrSeats}) => <Train from={from} to={to} nrSeats={nrSeats}/>)}
            </IonContent>
        </IonPage>
    );
};

export default Home;
